create table if not exists pangolin_jobs(id integer primary key autoincrement not null, job_key varchar(128) not null, source varchar(128) not null, attribute_json varchar(4096) null, cron_expression varchar(64) null, processor_key varchar(64) null, payload_json longvarchar(10240) null, status bigint null default 0, create_at timestamp not null, modify_at timestamp not null, constraint index_job_key unique (job_key));
create table if not exists pangolin_processors(id integer primary key autoincrement not null, processor_key varchar(128) not null, description varchar(128) null, source varchar(128) not null, attribute_json varchar(2048) null, processor_class varchar(256) not null, create_at timestamp not null, modify_at timestamp not null, constraint uk_processor_key unique (processor_key));
create table if not exists pangolin_tasks(id integer primary key autoincrement not null, url varchar(4096) null, host varchar(128) null, job_key varchar(128) not null, task_id varchar(128) not null, status bigint null default 0, task_obj blob null, create_at timestamp not null, extra_message varchar(4096) null default '', run_at timestamp null, finish_at timestamp null, modify_at timestamp not null, constraint uk_task_id unique (task_id));
